document.addEventListener('DOMContentLoaded', () => {
    const hamburger = document.querySelector('.hamburger');
    const menu = document.querySelector('nav');
    const navLinks = document.querySelectorAll('nav ul li a');
    const modal = document.getElementById('image-modal');
    const modalImage = document.getElementById('modal-image');
    const modalInfo = document.getElementById('image-info');
    const closeButton = document.querySelector('.close-button');
    const prevButton = document.querySelector('.prev-button');
    const nextButton = document.querySelector('.next-button');
    const galleryItems = document.querySelectorAll('.gallery-item img');
    const backToTop = document.getElementById('back-to-top');

    let currentIndex = 0;

    // Toggle navigation menu
    hamburger.addEventListener('click', () => {
        menu.classList.toggle('show');
    });

    // Close menu on link click
    navLinks.forEach(link => {
        link.addEventListener('click', () => {
            menu.classList.remove('show');
        });
    });

    // Open modal with image and info
    galleryItems.forEach((item, index) => {
        item.addEventListener('click', () => {
            openModal(index);
        });
    });

    // Close modal
    closeButton.addEventListener('click', closeModal);

    // Navigate to previous image
    prevButton.addEventListener('click', () => {
        navigateImage(-1);
    });

    // Navigate to next image
    nextButton.addEventListener('click', () => {
        navigateImage(1);
    });

    // Close modal on outside click
    window.addEventListener('click', (e) => {
        if (e.target === modal) {
            closeModal();
        }
    });

    // Show back to top button on scroll
    window.addEventListener('scroll', () => {
        if (window.scrollY > 200) {
            backToTop.style.display = 'block';
        } else {
            backToTop.style.display = 'none';
        }
    });

    // Scroll to top
    backToTop.addEventListener('click', () => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
    });

    // Keyboard navigation
    document.addEventListener('keydown', (e) => {
        if (modal.style.display === 'block') {
            if (e.key === 'ArrowLeft') {
                navigateImage(-1);
            } else if (e.key === 'ArrowRight') {
                navigateImage(1);
            } else if (e.key === 'Escape') {
                closeModal();
            }
        }
    });

    function openModal(index) {
        modal.style.display = 'block';
        currentIndex = index;
        updateModalContent();
        modal.setAttribute('aria-hidden', 'false');
    }

    function closeModal() {
        modal.style.display = 'none';
        modal.setAttribute('aria-hidden', 'true');
    }

    function navigateImage(direction) {
        currentIndex = (currentIndex + direction + galleryItems.length) % galleryItems.length;
        updateModalContent();
    }

    function updateModalContent() {
        modalImage.src = galleryItems[currentIndex].src;
        modalImage.alt = galleryItems[currentIndex].alt;
        modalInfo.textContent = galleryItems[currentIndex].getAttribute('data-info');
    }

    // Lazy loading images
    if ('IntersectionObserver' in window) {
        const imgObserver = new IntersectionObserver((entries, observer) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const img = entry.target;
                    img.src = img.dataset.src;
                    img.removeAttribute('data-src');
                    imgObserver.unobserve(img);
                }
            });
        });

        document.querySelectorAll('img[data-src]').forEach(img => {
            imgObserver.observe(img);
        });
    }
});
